mapper.insert:
insert into etv_auth_log (ID, APP_ID, MACHINE_NO, 
  SYSTEM_VERSION, AUTH_TYPE, AUTH_ACTIVE_KEY, 
  AUTH_STATE, IS_REPEAT_AUTH, IS_NEW, 
  RESULT_CODE, CREATE_TIME, RESULT_MSG
  )
values (#{id,jdbcType=VARCHAR}, #{appId,jdbcType=VARCHAR}, #{machineNo,jdbcType=VARCHAR}, 
  #{systemVersion,jdbcType=VARCHAR}, #{authType,jdbcType=VARCHAR}, #{authActiveKey,jdbcType=VARCHAR}, 
  #{authState,jdbcType=VARCHAR}, #{isRepeatAuth,jdbcType=INTEGER}, #{isNew,jdbcType=INTEGER}, 
  #{resultCode,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{resultMsg,jdbcType=LONGVARCHAR}
  )

mapper.select:
select APP_ID appId, MACHINE_NO machineNo, SYSTEM_VERSION sysVersion, AUTH_TYPE authType,
 CREATE_TIME createTime, cu.CU_NAME customerName,RESULT_MSG resultMsg,AUTH_STATE authState, 
 AUTH_ACTIVE_KEY authActiveKey,RESULT_CODE resultCode,IFNULL(IS_REPEAT_AUTH,'0') isRepeatAuth,cu.id cuId
from ETV_AUTH_LOG lg 
left join ETV_CUSTOMER cu on cu.CU_APPID=lg.APP_ID
where 1=1

	and cu.CU_APPID=#{appId,jdbcType=VARCHAR} 


	and AUTH_ACTIVE_KEY=#{activeKey,jdbcType=VARCHAR} 

 
 and RESULT_CODE=#{resCode,jdbcType=VARCHAR} 

 
 and AUTH_STATE=#{callBack,jdbcType=VARCHAR} 

 
 and MACHINE_NO like CONCAT(CONCAT('%', ltrim(rtrim(#{machineNo,jdbcType=VARCHAR}))),'%')


 and IS_REPEAT_AUTH = #{isRepeatAuth,jdbcType=INTEGER}

order by CREATE_TIME desc

mapper.update:
update ETV_AUTH_LOG
set AUTH_STATE = '1'

,IS_REPEAT_AUTH=#{isRepeatAuth,jdbcType=INTEGER}

where ID= #{logId,jdbcType=VARCHAR}

mapper.select:
 select max(CREATE_TIME),id
	from ETV_AUTH_LOG
	where APP_ID=#{appId,jdbcType=VARCHAR} and MACHINE_NO=#{machineNo,jdbcType=VARCHAR} 
	
		and SYSTEM_VERSION=#{systemVersion,jdbcType=VARCHAR}

select count(1) from ETV_AUTH_LOG
where APP_ID=#{appId,jdbcType=VARCHAR} and MACHINE_NO=#{machineNo,jdbcType=VARCHAR} 
and AUTH_STATE='1' and IS_NEW=1 and SYSTEM_VERSION=#{systemVersion,jdbcType=VARCHAR}
