mapper.insert:
insert into etv_auth_operate_log (id, customer_id, ps_id, 
  operate_detail, auth_type, auth_num, 
  remark, os, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{customerId,jdbcType=VARCHAR}, #{psId,jdbcType=VARCHAR}, 
  #{operateDetail,jdbcType=VARCHAR}, #{authType,jdbcType=VARCHAR}, #{authNum,jdbcType=INTEGER}, 
  #{remark,jdbcType=VARCHAR}, #{os,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 
aol.id,cu.cu_name customerName,operate_detail operateDetail,ps_username userName
, auth_type authType, auth_num authNum, create_time createTime,remark,os
from etv_auth_operate_log aol
inner join etv_customer cu on cu.id=aol.customer_id
inner join etv_personer ps on ps.id=aol.ps_id
where customer_id = #{customerId,jdbcType=VARCHAR}

and DATE_FORMAT(create_time,'%Y-%m-%d')>=#{startTime,jdbcType=VARCHAR}


and DATE_FORMAT(create_time,'%Y-%m-%d')<=#{endTime,jdbcType=VARCHAR}

order by create_time desc

select aol.id,cu.cu_name customerName,operate_detail operateDetail,ps_username userName , auth_type authType, auth_num authNum, create_time createTime,remark,os from etv_auth_operate_log aol inner join etv_customer cu on cu.id=aol.customer_id inner join etv_personer ps on ps.id=aol.ps_id where 1=1
and DATE_FORMAT(create_time,'%Y-%m-%d')>=#{startDate,jdbcType=VARCHAR}
and DATE_FORMAT(create_time,'%Y-%m-%d')<=#{endDate,jdbcType=VARCHAR}
and auth_type like CONCAT(CONCAT('%', ltrim(rtrim(#{type,jdbcType=VARCHAR}))),'%')
and operate_detail like CONCAT(CONCAT('%', ltrim(rtrim(#{operateType,jdbcType=VARCHAR}))),'%')
order by create_time desc

select aol.id,cu.cu_name customerName,operate_detail operateDetail,ps_username userName , auth_type authType, auth_num authNum, create_time createTime,remark,os from etv_auth_operate_log aol inner join etv_customer cu on cu.id=aol.customer_id inner join etv_personer ps on ps.id=aol.ps_id where 1=1
and DATE_FORMAT(create_time,'%Y-%m-%d')>=#{startTime,jdbcType=VARCHAR}
and DATE_FORMAT(create_time,'%Y-%m-%d')<=#{endTime,jdbcType=VARCHAR}
order by create_time desc
