mapper.insert:
insert into etv_email (id, user_name, company_name, 
  email, body_temperature, body_name, 
  body_equipment, body_photo_url, body_alarm_time, 
  send_state, send_callback, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{companyName,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{bodyTemperature,jdbcType=VARCHAR}, #{bodyName,jdbcType=VARCHAR}, 
  #{bodyEquipment,jdbcType=VARCHAR}, #{bodyPhotoUrl,jdbcType=VARCHAR}, #{bodyAlarmTime,jdbcType=VARCHAR}, 
  #{sendState,jdbcType=VARCHAR}, #{sendCallback,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from etv_email 
where user_name = #{userName,jdbcType=VARCHAR}

and send_state = #{state,jdbcType=VARCHAR}

order by create_time desc
