mapper.insert:
insert into etv_firmware (id, cpu_type, mac, 
  app_id, system_version, supplier_code, 
  create_time, check_time
  )
values (#{id,jdbcType=BIGINT}, #{cpuType,jdbcType=VARCHAR}, #{mac,jdbcType=VARCHAR}, 
  #{appId,jdbcType=VARCHAR}, #{systemVersion,jdbcType=VARCHAR}, #{supplierCode,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{checkTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT

FROM
etv_firmware where app_id=#{appId,jdbcType=VARCHAR}

  and mac = #{mac}


  and cpu_type = #{cpuType}

mapper.update:
update etv_firmware
set check_time=now()
where mac = #{mac,jdbcType=VARCHAR} and app_id=#{appId,jdbcType=VARCHAR}

mapper.select:
SELECT cpu_type cpuType, mac,
app_id appId, system_version systemVersion, supplier_code supplierCode,
create_time createTime, check_time checkTime
FROM
etv_firmware where  app_id=#{appId,jdbcType=VARCHAR}

  and cpu_type = #{cpuType}

SELECT

FROM
etv_firmware where  app_id = #{appId} and mac = #{mac}
