mapper.select:
SELECT 
	
FROM
	etv_mt_whiteboard_auth

	1=1
	
		and auth_state = #{authState}
	
	
		and cus_id = #{cusId}
	
	
		and app_key_type = #{appKeyType}
	
	
		and dev_mac like CONCAT(CONCAT('%', ltrim(rtrim(#{mac}))),'%')
	
	
	 	and create_time >= #{startDate} 
	
	
		and create_time <= #{endDate}

SELECT

FROM
etv_mt_whiteboard_auth
WHERE dev_mac = #{devMac}

	and appid = #{appid}

SELECT

FROM
etv_mt_whiteboard_auth
WHERE dev_mac = #{devMac} and app_key_type = #{appKeyType,jdbcType=INTEGER}

	and appid = #{appid}

SELECT cu_Name cnName, CU_COMPANY_NAME companyName, CU_APPID appid, CU_CREATE_TIME createTime, CU_PHONE phone, id cuId, (SELECT count(whiteboard_auth_id) FROM etv_mt_whiteboard_auth WHERE cus_id = id AND auth_state = - 1) unusedAuthNum, (SELECT count(whiteboard_auth_id) FROM etv_mt_whiteboard_auth WHERE cus_id = id ) totalAuthNum FROM etv_customer cus
CU_PS_ID = #{psId}
and CU_COMPANY_NAME like CONCAT(CONCAT('%', ltrim(rtrim(#{cpName}))),'%')
and CU_PHONE like CONCAT(CONCAT('%', ltrim(rtrim(#{phone}))),'%')
and CU_APPID = #{appid}

select count(1)
from etv_mt_whiteboard_auth
where APP_KEY = #{appKey,jdbcType=VARCHAR}

select whiteboard_auth_id id,app_key appKey,create_time createTime,app_key_type appKeyType from etv_mt_whiteboard_auth where ISNULL(CUS_ID)
and app_key_type=#{appKeyType}
order by whiteboard_auth_id desc

select 
*
from etv_mt_whiteboard_auth
where 
cus_id=#{cusId} and app_key_type='3'

	and auth_state=#{authState}


	 and create_time >= #{startDate} 


	and create_time <= #{endDate}
