mapper.select:
select 
 
from etv_sell_board
where 1=1

and mac like CONCAT(CONCAT('%', ltrim(rtrim(#{mac,jdbcType=VARCHAR}))),'%') 


and DATE_FORMAT(create_time,'%Y-%m-%d')>=#{startTime,jdbcType=VARCHAR}


and DATE_FORMAT(create_time,'%Y-%m-%d')<=#{endTime,jdbcType=VARCHAR}


and  use_app= #{useApp,jdbcType=VARCHAR}

order by create_time desc

select  from etv_sell_board
where mac = #{mac,jdbcType=VARCHAR} 

and  use_app= #{useApp,jdbcType=VARCHAR}
