CREATE TABLE `etv_client` (
  `ID` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '设备id',
  `CL_PS_ID` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '创建人id',
  `CL_GP_ID` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '分组id',
  `CL_NAME` varchar(100) COLLATE utf8_bin NOT NULL COMMENT '设备名称',
  `CL_NO` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '设备编号',
  `CL_MAC` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '设备mac',
  `CL_IP` varchar(2000) COLLATE utf8_bin NOT NULL COMMENT '设备ip',
  `CL_REMARK` varchar(100) COLLATE utf8_bin DEFAULT '' COMMENT '备注',
  `CL_DISK` varchar(20) COLLATE utf8_bin NOT NULL COMMENT '设备磁盘空间',
  `CL_LINE_STATE` varchar(2) COLLATE utf8_bin NOT NULL DEFAULT '1' COMMENT '是否离线状态（1在线-1离线）',
  `CL_VERSION` varchar(200) COLLATE utf8_bin NOT NULL COMMENT '设备版本',
  `CL_ADDRESS` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '地址',
  `CL_AUTH_STATE` varchar(2) COLLATE utf8_bin DEFAULT '-1' COMMENT '授权状态',
  `CL_CANCEL_TIME` datetime DEFAULT NULL COMMENT '注销时间',
  `CL_SYSTEM_VERSION` varchar(50) COLLATE utf8_bin DEFAULT NULL COMMENT '系统版本号',
  `Cl_STATE` varchar(2) COLLATE utf8_bin NOT NULL COMMENT '设备删除状态 1可用 -1已删除',
  `CL_SCREEN_NUM` varchar(5) COLLATE utf8_bin DEFAULT '1' COMMENT '屏幕个数',
  `CL_LATITUDE` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '经度',
  `CL_LONGITUDE` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '纬度',
  `Cl_RESOLUTION` varchar(50) COLLATE utf8_bin DEFAULT '' COMMENT '屏幕分辨率',
  `CL_CREATE_TIME` datetime NOT NULL COMMENT '注册时间',
  `Cl_UPDATE_TIME` datetime NOT NULL COMMENT '修改时间',
  `CL_WARNING_STATE` int(2) DEFAULT '0' COMMENT '丢失状态警报：0=正常，1=激活警报',
  `CL_ISOPEN_CALL` int(2) NOT NULL DEFAULT '0' COMMENT '是否开启报警电话',
  `CL_REPEAT_LINK_NUM` int(9) DEFAULT NULL,
  `CL_IM_REGISTER_STATE` int(2) DEFAULT '2' COMMENT '腾讯im注册状态 1=已注册 2=未注册 ',
  `CL_EXTEND_STATE` varchar(2) CHARACTER SET utf8 DEFAULT NULL COMMENT '扩展状态 1是正常在线离线状态  2是休眠 3是异常',
  PRIMARY KEY (`ID`),
  UNIQUE KEY `UQ_CL_NO` (`CL_NO`) USING BTREE,
  KEY `fk_PS_ID_RF_PERSONER_ID` (`CL_PS_ID`) USING BTREE,
  CONSTRAINT `etv_client_ibfk_1` FOREIGN KEY (`CL_PS_ID`) REFERENCES `etv_personer` (`ID`) ON DELETE CASCADE ON UPDATE CASCADE
